        API.getUsers().then(res => {
            console.log(res)
            this.setState({
                users: res.data.results,
                filteredUsers: res.data.results
            }) 
            console.log(this.state.users)
        })
    }
    handleSearchChange = event => {
        console.log(event.target.value)
