        const combinedList = this.state.users.filter(item => {
            let values = Object.values(item).join("").toLowerCase();
            return values.indexOf(filter.toLowerCase()) !== -1
        })
        console.log(combinedList)
        this.setState({filteredUsers: combinedList})
    }

